mapper.select:
select s.id,s.state,s.signDate,s.ip,s.signUserName, u.id unit_id, d.id document_id,d.DOCUMENT_TITLE, d.CREATE_DATETIME,d.publishUserName,d.remark,
pu.id publishUnit_id,pu.name publishUnit_name,
mf.id myFile_id,mf.fileName from signInfo s left join unit u on s.SIGN_UNIT_ID = u.id left join (document d left join unit pu on d.PUBLISH_UNIT_ID = pu.id left join myFile mf on d.id = mf.document_id ) on s.DOCUMENT_ID = d.id where s.id = #{id}

select id,state,signDate,ip,signUserName,DOCUMENT_ID,SIGN_UNIT_ID
from 
signInfo

	
		and state = #{state}
	
	
		and SIGN_UNIT_ID = #{signUnit.id}

  	select si.id,si.state,si.signDate,si.ip,si.signUserName,u.name unit_name,
  	d.CREATE_DATETIME,
  	d.DOCUMENT_TITLE,
  	d.doc_num,
  	d.LEVEL,
  	d.PUBLISHUSERNAME,
  	publishU.name publishUnit_name
  	from 
  	signInfo si left join unit u on si.SIGN_UNIT_ID = u.id
  	left join (document d left join unit publishU on d.PUBLISH_UNIT_ID = publishU.id ) on si.DOCUMENT_ID = d.id
  	
  		
  			
  				
  				and si.DOCUMENT_ID = #{paramEntity.document.id}
  			
  			
  				and d.DOCUMENT_TITLE like #{paramEntity.document.documentTitle}
  			
  			
  				
  					and publishU.name like #{paramEntity.document.publishUnit.name}
  				
  			
  		
  		
			
				
				and si.SIGN_UNIT_ID = #{paramEntity.signUnit.id}
			
		
		
			
			and si.state = #{paramEntity.state}
		
		
  			and d.create_Datetime  >= #{createDatetime_start}
  		
  		
  			and d.create_Datetime  <  #{createDatetime_end}
  		
  	
  	
		ORDER BY d.create_Datetime ${order}
		ORDER BY d.doc_Num ${order}
		ORDER BY d.level ${order}
		ORDER BY d.document_Title ${order}
		ORDER BY publishU.name ${order}
		ORDER BY d.publishUserName ${order}
		
	
  	limit #{start},#{rows}

  	select count(si.id) 
  	from 
  	signInfo si left join unit u on si.SIGN_UNIT_ID = u.id
  	left join (document d left join unit publishU on d.PUBLISH_UNIT_ID = publishU.id ) on si.DOCUMENT_ID = d.id
  	
  		
  			
  				
  				and si.DOCUMENT_ID = #{paramEntity.document.id}
  			
  		
  		
			
				
				and si.SIGN_UNIT_ID = #{paramEntity.signUnit.id}
			
		
		
			
			and si.state = #{paramEntity.state}
